SDpagedisplay[SDpagenum] = "Slide24.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'amelioration et securisation de la prise en charge medicamenteuse et du circuit du medicament et des produits et prestations comprend differents&lt;/span&gt;&lt;span &gt; axes qui sont:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- l'informatisation du circuit du medicament et des dispositifs medicaux ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- le developpement de la prescription et de la dispensation a delivrance nominative ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- la tracabilite, de la prescription a l'administration, pour les medicaments ou a l'utilisation pour les dispositifs medicaux ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- le developpement d'un systeme d'assurance de la qualite ;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- et enfin la centralisation de la preparation des medicaments, sous la responsabilite d'un pharmacien, en particulier des traitements anticancereux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
