SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le pharmacien hospitalier est investi dans l'ensemble de ces 5 thematiques. neanmoins, dans cette sequence, nous allons plus particulierement detailler les activites pharmaceutiques specifiques aux 2 premiers points.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
