SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;maintenant&lt;/span&gt;&lt;span &gt; que nous avons detailler les elements de politique generale de l'etablissement en termes de gestion du risque, je vous propose de nous consacrer aux activites pharmaceutiques destinees a ameliorer et securiser la prise en charge medicamenteuse et le circuit du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
