SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide31.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;revenons maintenant&lt;/span&gt;&lt;span &gt; a la delivrance nominative.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la delivrance nominative represente un mode d'organisation du circuit du medicament qui permet de lutter contre l'iatrogenie medicamenteuse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parmi ses missions, la pharmacie a usage interieur doit assurer la dispensation des medicaments pour les malades hospitalises. cette dispensation comprend la delivrance nominative des medicaments associee a l'analyse pharmaceutique de la prescription. malgre les evolutions reglementaires, la delivrance des medicaments par la pharmacie aux unites de soins est encore majoritairement organisee selon un mode global, a savoir commande globale de l'unite de soins a la pharmacie puis approvisionnement de la reserve en medicaments du service par la pharmacie. les choses ont change avec la mise en place de la delivrance individuelle et nominative, qui peut etre hebdomadaire ou journaliere.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le principal avantage de la delivrance individuelle et nominative, outre l'analyse pharmaceutique des prescriptions, est de fournir les medicaments identifies au nom de chaque malade. on limite ainsi la manipulation des medicaments par le personnel infirmier. cette activite est assuree par les preparateurs en pharmacie, sous responsabilite du pharmacien hospitalier. le role du personnel infirmier est alors la distribution et l'administration des medicaments aux malades. ce dispositif permet de liberer du temps au personnel infirmier au profit des taches de soins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@delivrance nominative@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide32.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici deux exemples&lt;/span&gt;&lt;span &gt; du rendu final: a gauche un pilulier, a droite un sachet. les deux permettent d'indiquer le nom du patient sur le dispositif et donc de securiser la dispensation et l'administration. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
