SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la mise en place de la delivrance individuelle et nominative peut se faire selon differentes modalites, soit centralisee au sein de la pharmacie centrale, soit delocalisee au sein d'une antenne&lt;/span&gt;&lt;span &gt; pharmaceutique&lt;/span&gt;&lt;span &gt;. elle peut d'autre par etre assuree dans le cadre d'une preparation manuelle des doses par les preparateurs en pharmacie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

