SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide34.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en opposition,&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;la delivrance nominative peut etre realisee/assistee par un automate de delivrance individuelle. elle&lt;/span&gt;&lt;span &gt; peut etre realisee dans la pharmacie centrale ou dans une antenne pharmaceutique. seule la taille de l'automate change. &lt;/span&gt;&lt;span &gt;les automates de delivrance individuelle assurent le conditionnement unitaire des doses de medicament pour chaque patient, a partir de la pharmacie centrale. ils permettent ainsi une reduction des erreurs de delivrance par la pharmacie et un gain de temps pour les preparateurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@delivrance nominative centralisee manuelle@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide35.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la delivrance individuelle centralisee manuelle est la premiere a avoir ete testee. malgre les nombreuses experiences anglo-saxonnes, ce mode d'organisation reste peu developpe, en raison notamment du temps supplementaire necessaire en preparateur en pharmacie et en pharmacien. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de plus, cette organisation ne permet pas une reactivite importante, notamment en cas de modifications frequentes de la prescription. ainsi, elle est peu adaptee aux unites de soins « aigues ». en revanche, elle particulierement bien adaptee aux unites d'hospitalisation de moyens et de long sejour comme par exemple la geriatrie, les soins de suite et readaptation ou encore la psychiatrie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce mode d'organisation peut-etre optimise par l'utilisation de stockeurs rotatifs de medicaments qui facilitent la cueillette des medicaments par les preparateurs en limitant leurs deplacements.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@la delivrance individuelle decentralisee automatisee@";
SDpagedocid[SDpagenum] = SDdocnum;
