SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide36.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la delivrance individuelle decentralisee est particulierement adaptee aux antennes&lt;/span&gt;&lt;span &gt; pharmaceutiques gerant des unites de soins &lt;/span&gt;&lt;span &gt;avec des patients dont l'etat clinique n'est pas stable et pour lesquels la prescription medicamenteuse change regulierement. cette organisation &lt;/span&gt;&lt;span &gt;cumule les avantages lies a l'automatisation et ceux&lt;/span&gt;&lt;span &gt; lies a&lt;/span&gt;&lt;span &gt; la reactivite des antennes pharmaceutiques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@armoires a pharmacie securisees@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide37.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour finir, voici un autre dispositif que vous pouvez trouver dans les unites de soins. il s'agit des armoires&lt;/span&gt;&lt;span &gt; a pharmacie securisee. il est possible de remplacer les armoires a pharmacie classiques des unites de soins par des automates de distribution de medicaments, plus couramment appeles armoires a pharmacie securisees. ces dispositifs commandes par un ordinateur, sont directement reapprovisionnes par des preparateurs en pharmacie et securisent le stockage des medicaments dans l'unite de soins. ils facilitent et guident la selection des medicaments par les infirmiers pour chaque patient, assurant ainsi une delivrance nominative. cette aide a la cueillette des medicaments limite le risque de confusion entre deux dosages et assure une tracabilite totale de l'acces aux medicaments (personnel soignant et patient).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est un dispositif different&lt;/span&gt;&lt;span &gt; de ce que nous avons detaille precedemment.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

