SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;toutes ces possibilites technologiques participent a la rationalisation et l'efficience de la mise a disposition du medicament. ces dispositions techniques et organisationnelles doivent evidemment etre accompagnees par le pharmacien hospitalier et lui permettre de liberer du temps pour se rapprocher de l'unite de soins, lieu ideal pour une securisation adaptee et proactive du patient. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vous propose&lt;/span&gt;&lt;span &gt; maintenant de nous interesser a des activites de pharmacie clinique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
