SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;illustrons de nouveau avec un exemple s'interessant a l'etape de prescription.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les logiciels&lt;/span&gt;&lt;span &gt; de prescriptions peuvent mettre a disposition des prescripteurs des recommandations en ligne ou &lt;/span&gt;&lt;span &gt;des rappels specifiques.&lt;/span&gt;&lt;span &gt; leur objectif est &lt;/span&gt;&lt;span &gt;d'eviter une prescription non adaptee. ainsi, ces recommandations en lignes et rappels&lt;/span&gt;&lt;span &gt; sont chacun des tranches du fromage du modele de &lt;/span&gt;&lt;span &gt;reason&lt;/span&gt;&lt;span &gt;. ces sont 2 barrieres de securites.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, le systeme informatique est defectueux. le blocage par rappel electronique n'a donc pas fonctionne et l'erreur de prescription s'est produite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la tranche suivante doit normalement stopper l'erreur,&lt;/span&gt;&lt;span &gt; de maniere a ce qu'elle soit potentielle et non averee&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
