SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette typologie est essentiellement &lt;/span&gt;&lt;span &gt;issue &lt;/span&gt;&lt;span &gt;d'une taxonomie americaine construite &lt;/span&gt;&lt;span &gt;par le ncc merp.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une autre instance qui a beaucoup travaille sur cette typologie est le reseau epidemiologique des erreurs medicamenteuses (reem). le reem a ete cree en 1998 par l'association pour l'assurance qualite en therapeutique et l'evaluation. des programmes de meme nature existent dans plusieurs pays ou la problematique de l'erreur medicamenteuse est deja geree, parfois depuis longtemps. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
