SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;enfin, la classification&lt;/span&gt;&lt;span &gt; integre les causes identifiees associees a la survenue de l'erreur medicamenteuse. il peut s'agir de problemes de communication, de confusion denomination, de problemes d'etiquetage, de problemes d'information, de problemes de conditionnement ou de conception ou de facteurs humains.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
