SDpagedisplay[SDpagenum] = "Slide30.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;apres les causes liees a l'humain et celles liees aux produits, les facteurs lies a l'environnement de travail sont le 3&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; type de causes d'erreurs medicamenteuses a noter.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un environnement de travail avec beaucoup de bruit, avec de nombreuses interruptions de taches et de multiples distractions, avec un manque d'effectif, des defauts de communication, avec une pression par l'urgence du processus de soins sont autant de defaillances latentes pouvant a terme etre source d'erreurs medicamenteuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
