SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans ce cours, nous avons detailler&lt;/span&gt;&lt;span &gt; le circuit du medicament et ses 5 etapes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous avons vu deux outils utilisables dans le cadre de la demarche qualite associee aux erreurs medicamenteuses. il s'agit du &lt;/span&gt;&lt;span &gt;diagramme d'&lt;/span&gt;&lt;span &gt;hischikawa&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;qui nous permet de travailler sur une erreur medicamenteuse potentielle qui n'est jamais survenue en identifiant et listant l'ensemble des causes possible&lt;/span&gt;&lt;span &gt;s et le &lt;/span&gt;&lt;span &gt;modele de &lt;/span&gt;&lt;span &gt;reason&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;qui &lt;/span&gt;&lt;span &gt;permet d'analyser a posteriori la survenue d'une erreur &lt;/span&gt;&lt;span &gt;medicamenteuse&lt;/span&gt;&lt;span &gt;modele&lt;/span&gt;&lt;span &gt; de &lt;/span&gt;&lt;span &gt;reason&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, &lt;/span&gt;&lt;span &gt;la classification ncc-merp permet de categoriser et classifier les erreurs medicamenteuses notamment&lt;/span&gt;&lt;span &gt; suivant les c&lt;/span&gt;&lt;span &gt;onsequences cliniques pour le patient et les c&lt;/span&gt;&lt;span &gt;auses identifiees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
