SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous venons de voir ensemble la complexite du processus de soins qui fait intervenir des patients, des soignants, des equipes, des taches complexes, des conditions de travail parfois sous-optimales, des organisations pouvant elles aussi ameliorees et un contexte institutionnel. aussi de plus en plus de strategies globales impliquant l'ensemble des professionnels de sante sont mises en place pour prevenir ensemble l'iatrogenie medicamenteuse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est ce que nous allons aborder dans la 4&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; sequence&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;de ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
