SDpagetitle[SDpagenum] = "@iatrogenie medicamenteuse: quelle ampleur?@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'iatrogenie&lt;/span&gt;&lt;span &gt; medicamenteuse a fait l'objet de nombreuses etudes internationales depuis les annees 90. ce sont les etats-uniens qui les premiers publierent sur ce sujet.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en france, depuis les annees 2000, nous disposons de plusieurs etudes qui nous aide a comprendre le phenomene. ils s'agit essentiellement des etudes eneis 1 et 2, emir, evisa et esprit. elles ont ete conduites aussi en secteur hospitalier qu'en ville.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iatrogenie medicamenteuse: quelle ampleur?@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons particulierement nous interesser aux resultats de l'enquete nationale sur les evenements indesirables graves lies aux soins, aussi appelee enquete eneis. l'etude eneis de 2004 (dite eneis 1 par la suite) a ete reconduite selon la meme methodologie entre avril et juin 2009. cette seconde etude est dite eneis 2 par la suite. ces deux etudes sont celles qui ont permis de preciser l'ampleur de l'iatrogenie, notamment medicamenteuse en france et son evolution.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@eneis 1 et 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces deux enquetes ont porte portant sur plus 70 etablissements de sante francais. les echantillons etaient respectivement de 8754 patients pour eneis1 et 8269 pour eneis2, repartis respectivement dans 292 et 251 unites de soins. les patients etaient suivis pendant une periode maximale de 7 jours. ainsi, au total plus de 30.000 journees d'hospitalisation observees pour chacune des deux enquetes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@eneis 1 et 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'objectif general de ces deux etudes est de mettre en evidence l'importance de l'iatrogenie en france et plus particulierement de l'iatrogenie medicamenteuse. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elles se sont donc interessees a etudier les evenements indesirables graves responsables d'hospitalisation, ceux survenus en cours d'hospitalisation et leur evolution entre 2004 et 2009. l'etude s'est deroulee aussi bien en service de chirurgie qu'en service de medecine. tous les evenements indesirables graves etaient consideres dans les deux etudes, qu'ils soient lies a un acte de soins invasif ou non, a un produit de sante ou a une infection associee aux soins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@eneis 1 et 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;globalement, les etudes eneis 1 et 2, aux resultats complementaires, distinguent les evenements indesirables graves responsables d'hospitalisation de ceux qui sont survenus en cours d'hospitalisation. en plus de cette distinction,  les deux etudes se sont interessees a leur caractere evitable ou non.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@eneis 1 et 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;commencons par regarder les resultats totaux et plus specifiquement les evenements indesirables graves causes d'hospitalisation. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour l'annee 2004, en moyenne, dans l'ensemble des etablissements, on peut estimer que 3,9 % des sejours sont causes par un evenement indesirable grave . si on se restreint aux evenements indesirables graves evitables, la proportion de sejours causes par des evenements indesirables graves est de 1,8 %.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour l'annee 2009, en moyenne, dans l'ensemble des etablissements, on peut estimer que 4,5 % des sejours sont causes par un evenement indesirable grave.  si on se restreint aux evenements indesirables graves evitables, la proportion de sejours causes par des evenements indesirables graves est de 2,6 %.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces resultats sont similaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@eneis 1 et 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide8.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;poursuivions avec les evenements indesirables graves survenus en cours d'hospitalisation. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour l'annee 2004, on peut estimer que le taux d'incidence des evenements indesirables graves survenus en cours d'hospitalisation est de 6,6‰. si on se restreint aux evenements indesirables graves evitables, le taux d'incidence est de 2,3‰.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour l'annee 2009, on peut estimer que le taux d'incidence des evenements indesirables graves survenus en cours d'hospitalisation est de 6,2‰. si on se restreint aux evenements indesirables graves evitables, le taux d'incidence est de 2,6‰.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la encore, les resultats entre 2004 et 2009 sont similaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
