SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;evisa a ete conduite en 2008, en aquitaine, aupres de 2 946 patients, suivant une methodologie comparable a celle d'eneis. elle s'interesse aux evenements indesirables&lt;/span&gt;&lt;span &gt; lies aux soins en ville. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'interet de&lt;/span&gt;&lt;span &gt; l'etude evisa reside dans &lt;/span&gt;&lt;span &gt;l'analyse approfondie des causes associees aux&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;evenements indesirables evitables.&lt;/span&gt;&lt;span &gt; ainsi, dans les trois principales causes identifiees figurent &lt;/span&gt;&lt;span &gt;l'erreur d'indication therapeutique dans&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;30 % des cas et le manque de surveillance de l'etat de sante dans&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;19 % des cas.&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;enfin, outre ces causes principales, le defaut de communication entre soignants est un facteur contributif qui a ete observe dans 28 % des cas.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
