SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons fini cette&lt;/span&gt;&lt;span &gt; sequence qui a trait a l'epidemiologie des evenements indesirables grave en france. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les 2 etudes eneis sont parmi les points essentiels de ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est bon de garder a l'esprit que le medicament est responsable du 1/3 des &lt;/span&gt;&lt;span &gt;evenements indesirables graves cause d'hospitalisations et du &lt;/span&gt;&lt;span &gt;1/5&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;des &lt;/span&gt;&lt;span &gt;evenements indesirables graves en cours d'hospitalisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin,&lt;/span&gt;&lt;span &gt; et peut-etre le plus important, la moitie de ces &lt;/span&gt;&lt;span &gt;evenements indesirables graves sont evitables.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la prochaine sequence est consacree&lt;/span&gt;&lt;span &gt; aux erreurs medicamenteuses et a &lt;/span&gt;&lt;span &gt;leur classification.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
