SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le cours&lt;/span&gt;&lt;span &gt; icm4 comprend 3 chapitres qui sont:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les definitions en lien avec l'iatrogenie medicamenteuse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'epidemiologie de l'iatrogenie medicamenteuse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;partie 3: classification des erreurs medicamenteuses&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et enfin, les&lt;/span&gt;&lt;span &gt; s&lt;/span&gt;&lt;span &gt;trategies de prevention de l'iatrogenie medicamenteuse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
