SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce&lt;/span&gt;&lt;span &gt; chapitre est donc &lt;/span&gt;&lt;span &gt;specifiquement&lt;/span&gt;&lt;span &gt; dedie aux definitions. il a pour objectifs de vous permettre de &lt;/span&gt;&lt;span &gt;connaitre les termes associes a l'iatrogenie medicamenteuse et de savoir identifier les effets indesirables, les erreurs medicamenteuse et les evenements indesirables medicamenteux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
