SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un evenement indesirable associe aux soins peut&lt;/span&gt;&lt;span &gt; etre egalement qualifie de &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;grave&lt;/span&gt;&lt;span &gt;. a ce titre, il repond a une definition stricte du code de sante publique. il s'agit d'un e&lt;/span&gt;&lt;span &gt;venement &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;inattendu&lt;/span&gt;&lt;span &gt; au regard de l'etat de sante et de la pathologie de la personne.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette definition tient compte du &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;contexte&lt;/span&gt;&lt;span &gt; et des &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;consequences&lt;/span&gt;&lt;span &gt; pour le patient.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ainsi,&lt;/span&gt;&lt;span &gt; u&lt;/span&gt;&lt;span &gt;n evenement indesirable grave associe a des soins realises lors d'investigations, de traitements, d'actes medicaux a visee esthetique ou d'actions de prevention.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
