SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;maintenant&lt;/span&gt;&lt;span &gt; que nous avons precise le contexte de survenue des evenements indesirables graves associes aux soins, qu'en est-il des consequences pour le patient?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la qualification de &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;grave&lt;/span&gt;&lt;span &gt; en utilise pour caracteriser un evenement indesirable grave associe aux soins en cas de deces, de mise en jeu du pronostic vital, de survenue probable d'un deficit fonctionnel permanent y compris une anomalie ou une malformation congenitale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
