SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons clairement pose la definition d'un evenement indesirable associe aux soins. a partir de maintenant, dans le cours d'icm4, nous ne nous interesserons qu'aux evenements indesirables medicamenteux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
