SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un evenement indesirable medicamenteux peut etre issu de deux situations differentes non mutuellement exclusives:  l'erreur medicamenteuse et l'effet indesirable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons maintenant detailler successivement les definitions en lien avec les effets indesirables et les erreurs medicamenteuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
