SDpagedisplay[SDpagenum] = "Slide14.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;commencons par celles de l'effet indesirable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;selon la definition commune a l'organisation mondiale de la sante et a l'union europeenne, l'effet indesirable est une reaction &lt;/span&gt;&lt;span  fontweight='bold'&gt;nocive&lt;/span&gt;&lt;span &gt; et &lt;/span&gt;&lt;span  fontweight='bold'&gt;non voulue&lt;/span&gt;&lt;span &gt; a un medicament. cette reaction se produit aux posologies normalement utilisees chez l'homme pour la prophylaxie, le diagnostic ou le traitement d'une maladie ou pour la restauration, la correction ou la modification d'une fonction physiologique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la definition francaise est elargie puisqu'elle mentionne le fait que l'effet indesirable peut resulter d'un mesusage du medicament ou du produit. le mesusage correspond au non respect &lt;/span&gt;&lt;span  fontweight='bold'&gt;intentionnel&lt;/span&gt;&lt;span &gt; des recommandations du resume des caracteristiques du produit par un professionnel de sante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@effet indesirable (ei)@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
