SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide16.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans certaines circonstances, un effet indesirable peut etre qualifie de grave.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un effet indesirable grave est un effet indesirable qui entraine le deces du patient ou qui est susceptible de mettre en danger sa vie.&lt;/span&gt;&lt;span &gt; il est egalement qualifie de grave s'il ent&lt;/span&gt;&lt;span &gt;raine une invalidite ou une incapacite importantes ou durables, s'il provoque ou prolonge une hospitalisation ou s'il entraine une anomalie ou une malformation congenitale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour illustrer cette definition, prenons le cas d'une hemorragie digestive survenue chez un patient suite a la prise de &lt;/span&gt;&lt;span &gt;ketoprofene&lt;/span&gt;&lt;span &gt;, un anti-inflammatoire non steroidien. cette hemorragie entraine l'hospitalisation du patient en urgence avec la realisation d'une fibroscopie digestive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans ce cas, l'hemorragie digestive constitue l'effet indesirable. il est rare et decrit dans le rcp du produit. et c'est bien le fait d'etre hospitalise qui confere le caractere dit de grave a cet effet indesirable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

